import React, {useEffect} from "react";
import InfiniteScroll from 'react-infinite-scroll-component';
import { ReactTinyLink } from 'react-tiny-link'
import { makeStyles } from "@material-ui/core/styles";
import Axios from "axios"

import 'react-vertical-timeline-component/style.min.css';
import styles from "assets/jss/material-dashboard-react/components/bodyStyle.js";


const useStyles = makeStyles(styles)

export default function Knowledge() {

  const classes = useStyles();
  const [recommends, setRecommendations] = React.useState([]);
  const state = {
    items: Array.from({ length: 6 })
  };

  useEffect(() => {
    const params = {
      category: 'knowledge'
    }

    Axios.post(`/api/hacks/getRecommends`,  params)
    .then(res => {
        setRecommendations(res.data)
        console.log(res.data)
    }).catch(err => {
      console.log(err)
    })

  }, [])

  const fetchMoreData = () => {
    // a fake async api call like which sends
    // 20 more records in 1.5 secs
    setTimeout(() => {
      this.setState({
        items: this.state.items.concat(Array.from({ length: 6 }))
      });
    }, 1500);
  };



  return (



    <div className={classes.categoryWrapper}>
      <InfiniteScroll
          dataLength={this.state.items.length}
          next={fetchMoreData}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >

     {recommends && recommends.length > 0 && recommends.map((rec, index) => (

        <div key={index} className={classes.tinyLink}>


         <ReactTinyLink
           cardSize="small"
           showGraphic={true}
           maxLine={2}
           minLine={1}
           description={rec.description}
           url={rec.link}
         />
         </div>

     ))}

</InfiniteScroll>









    </div>


  );
}
