import React, {useEffect} from "react";
import InfiniteScroll from 'react-infinite-scroll-component';
import { ReactTinyLink } from 'react-tiny-link'
import { makeStyles } from "@material-ui/core/styles";
import Axios from "axios"

import 'react-vertical-timeline-component/style.min.css';
import styles from "assets/jss/material-dashboard-react/components/bodyStyle.js";


const useStyles = makeStyles(styles)

export default function Knowledge() {

  const classes = useStyles();
  const [recommends, setRecommendations] = React.useState([]);

  useEffect(() => {
    const params = {
      category: 'knowledge'
    }

    Axios.post(`/api/hacks/getRecommends`,  params)
    .then(res => {
        setRecommendations(res.data)
        console.log(res.data)
    }).catch(err => {
      console.log(err)
    })

  }, [])





  return (



    <div className={classes.categoryWrapper}>


<div id="scrollableDiv" style={{height: 300, overflow: 'auto', display: 'flex', flexDirection: 'column-reverse'}}>
    {/*Put the scroll bar always on the bottom*/}
    <InfiniteScroll
      dataLength={this.state.items.length}
      next={this.fetchMoreData}
      style={{ display: 'flex', flexDirection: 'column-reverse' }} //To put endMessage and loader to the top.
      inverse={true} //
      hasMore={true}
      loader={<h4>Loading...</h4>}
      scrollableTarget="scrollableDiv"
    >
     {recommends && recommends.length > 0 && recommends.map((rec, index) => (

        <div key={index} className={classes.tinyLink}>


         <ReactTinyLink
           cardSize="small"
           showGraphic={true}
           maxLine={2}
           minLine={1}
           description={rec.description}
           url={rec.link}
         />
         </div>

     ))}
    </InfiniteScroll>
  </div>









    </div>


  );
}
