import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from '@material-ui/core/Paper';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/MailOutline';
import detectBrowserLanguage from 'detect-browser-language'
import { ValidatorForm, TextValidator} from 'react-material-ui-form-validator'

import 'react-vertical-timeline-component/style.min.css';
import styles from "assets/jss/material-dashboard-react/components/bodyStyle.js";
const useStyles = makeStyles(styles);

export default function Knowledge() {
  const classes = useStyles();
  return (
    <div>
      <Paper className={classes.form}>
       Do you wanna receive the newest platforms to boost your startup in your e-mail?

        <Grid container spacing={1} alignItems="flex-end" className={classes.gridMail}>
          <Grid item>
            <AccountCircle />
          </Grid>
          <Grid item>
              <ValidatorForm
                  ref="form"
                  onSubmit={this.handleSubmit}
                  onError={errors => console.log(errors)}
              >
              <TextField className={classes.textFieldMail} id="input-with-icon-grid" label="Enter your best e-mail" />
              <TextValidator
                  label="Your e-mail, please"
                  onChange={this.handleChange}
                  name="email"
                  value={this.state.formData.email}
                  validators={['required', 'isEmail']}
                  errorMessages={['This field is required', 'Please insert a valid e-mail address']}
                  className={classes.textFieldMail} 
                  required
              />
              </ValidatorForm>
          </Grid>
        </Grid>
      </Paper>

    </div>
  );
}
