import React, {useEffect} from "react";
import detectBrowserLanguage from 'detect-browser-language'
import PublicIcon from '@material-ui/icons/Public';
import IconButton from '@material-ui/core/IconButton';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';

import Typography from '@material-ui/core/Typography';
import Axios from "axios"

import 'react-vertical-timeline-component/style.min.css';
import styles from "assets/jss/material-dashboard-react/components/bodyStyle.js";



const handleImageError = (e) => {
  e.target.onerror = null;
  e.target.src = `${process.env.PUBLIC_URL}/filesystem/icons/default.png`
}


const useStyles = makeStyles(styles)


function usePrevious(data){
  const ref = React.useRef();
  React.useEffect(()=>{
    ref.current = data
  }, [data])
  return ref.current
}

export default function Knowledge() {

  const classes = useStyles();
  const [recommends, setRecommendations] = React.useState([]);


  const [count, setCount] = React.useState(0)

  const _increaseCount = () => {
    setCount(count + 3)
  }

  const prevCount = usePrevious(count)







  useEffect(() => {

    var params = {}
    if(detectBrowserLanguage().toLowerCase() === "pt-br"){
      params = {
        category: 'knowledge',
        language: 'pt'
      }
    } else {
      params = {
        category: 'knowledge',
        language: 'en'
      }
    }


    Axios.post(`/api/hacks/getRecommends`,  params)
    .then(res => {
        setRecommendations(res.data)
        console.log(res.data)
    }).catch(err => {
      console.log(err)
    })

  }, [])




  return (


    <div className={classes.categoryWrapper}>


      <div>Previous Count Value: {prevCount}</div>
      <div>Current Count Value: {count}</div>
      <button onClick={_increaseCount}>Load More</button>


     {recommends && recommends.length > 0 && recommends.map((rec, index) => (

        <a key={index} href={`${rec.link}`} target="_blank" rel="noopener noreferrer">
          <Card key={index} className={classes.card}>
          <CardActionArea>
          <CardMedia
            component="img"
            alt="s"
            height="140"
            image={`${process.env.PUBLIC_URL}/filesystem/icons/${rec.id}.png`}
            title=""
            onError={handleImageError}  
          />

            <CardContent>
              <Typography gutterBottom variant="h5" component="h2" style={{textAlign: 'center', fontSize: '20px'}}>
                {rec.title}
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p" style={{height: '100px', textAlign: 'center'}}>
                {rec.description}
              </Typography>
            </CardContent>
          </CardActionArea>
          <CardActions>
            <IconButton aria-label="add to favorites">
            <PublicIcon />
          </IconButton>
            &nbsp;{rec.views} views
          </CardActions>
        </Card>
      </a>
     ))}

    </div>


  );
}
