import React, {useEffect, useRef} from "react";
import { ReactTinyLink } from 'react-tiny-link'
import { makeStyles } from "@material-ui/core/styles";
import Axios from "axios"

import 'react-vertical-timeline-component/style.min.css';
import styles from "assets/jss/material-dashboard-react/components/bodyStyle.js";


const useStyles = makeStyles(styles)

export default function Knowledge() {

  const classes = useStyles();
  const [recommends, setRecommendations] = React.useState([]);
  const [visible, setVisible] = React.useState(6);

  const prevCountRef = useRef();
  useEffect(() => {
    const params = {
      category: 'knowledge'
    }

    Axios.post(`/api/hacks/getRecommends`,  params)
    .then(res => {
        setRecommendations(res.data)
        console.log(res.data)
    }).catch(err => {
      console.log(err)
    })

    prevCountRef.current = visible;
  }, [])

  const loadMore = (prev) => {
    const ref = useRef();
    const newvisible = ref + 6
    setVisible(newvisible)
  }


  return (



    <div className={classes.categoryWrapper}>


     {recommends && recommends.length > 0 && recommends.map((rec, index) => (

        <div key={index} className={classes.tinyLink}>


         <ReactTinyLink
           cardSize="small"
           showGraphic={true}
           maxLine={2}
           minLine={1}
           description={rec.description}
           url={rec.link}
         />
         </div>

     ))}

      {visible < recommends.length &&
          <Button
          variant="contained"
          color="default"
          startIcon={<Cached />}
          onClick={loadMore}
          className="load-more-wrapper"
          >
          load more enterprises
          </Button>
      }

    </div>


  );
}
