import React, {useEffect} from "react";
import { ReactTinyLink } from 'react-tiny-link'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Axios from "axios"

import 'react-vertical-timeline-component/style.min.css';
import styles from "assets/jss/material-dashboard-react/components/bodyStyle.js";



const handleImageError = (e) => {
  e.target.onerror = null;
  e.target.src = `${process.env.PUBLIC_URL}/filesystem/icons/default.png`
}


const useStyles = makeStyles(styles)


export default function Knowledge() {

  const classes = useStyles();
  const [recommends, setRecommendations] = React.useState([]);

  useEffect(() => {
    const params = {
      category: 'knowledge'
    }

    Axios.post(`/api/hacks/getRecommends`,  params)
    .then(res => {
        setRecommendations(res.data)
        console.log(res.data)
    }).catch(err => {
      console.log(err)
    })
    console.log(defaultimg)

  }, [])




  return (



    <div className={classes.categoryWrapper}>

     {recommends && recommends.length > 0 && recommends.map((rec, index) => (

        <Card key={index} className={classes.card}>
        <CardActionArea>
        <CardMedia
          component="img"
          alt="s"
          height="140"
          image={`${process.env.PUBLIC_URL}/filesystem/icons/${rec.id}.png`}
          title=""
          onError={handleImageError}  
        />

          <CardContent>
            <Typography gutterBottom variant="h5" component="h2">
              Lizard
            </Typography>
            <Typography variant="body2" color="textSecondary" component="p">
              Lizards are a widespread group of squamate reptiles, with over 6,000 species, ranging
              across all continents except Antarctica
            </Typography>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button size="small" color="primary">
            Share
          </Button>
          <Button size="small" color="primary">
            Learn More
          </Button>
        </CardActions>
      </Card>

     ))}


    </div>


  );
}
