import React, {useState} from "react";
import { makeStyles } from "@material-ui/core/styles";
import Paper from '@material-ui/core/Paper';
import {TextField, FormHelperText} from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/MailOutline';
import detectBrowserLanguage from 'detect-browser-language'
import Button from '@material-ui/core/Button';
import { AvForm, AvField } from 'availity-reactstrap-validation';


import 'react-vertical-timeline-component/style.min.css';
import styles from "assets/jss/material-dashboard-react/components/bodyStyle.js";
const useStyles = makeStyles(styles);

export default function Knowledge() {
  const classes = useStyles();
  const [email, setEmail] = useState('')


  const handleChangeEmail = (email) => {
    setEmail(email)
  }

  const validateAndSubmit = (event, errors, values) => {
    console.log(email)
  }

  return (
    <div>
      <Paper className={classes.form}>
       Do you wanna receive the newest platforms to boost your startup in your e-mail?

        <Grid container spacing={2} alignItems="flex-end" className={classes.gridMail}>
          <AvForm className="retrieve-from-email" onSubmit={validateAndSubmit} >

            <Grid item>
              <AccountCircle />
            </Grid>
            <Grid item>



                <AvField 
                  onChange={e => handleChangeEmail(e.target.value)} 
                  type="email" 
                  name="retrieve-email" 
                  className="form-control"  
                  placeholder="Enter e-mail"  
                  required />

            </Grid>
            <Grid item>
              <Button variant="contained" color="primary" onClick={validateAndSubmit}>
                Subscribe Me
              </Button>
            </Grid>
          </AvForm>
        </Grid>
      </Paper>

    </div>
  );
}
